import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from "sweetalert2"
import withReactContent from "sweetalert2-react-content"
import "./Login.css"

import { initializeApp } from "https://www.gstatic.com/firebasejs/10.5.0/firebase-app.js";

const firebaseConfig = {
    apiKey: "AIzaSyB1n5GjyWpRr4sC3mo9AjL0SkOH3KC5ZVI",
    authDomain: "basic-web-2b71e.firebaseapp.com",
    projectId: "basic-web-2b71e",
    storageBucket: "basic-web-2b71e.appspot.com",
    messagingSenderId: "60043802817",
    appId: "1:60043802817:web:f0a59ba7d8b09c0d09bc40",
    measurementId: "G-HGEGEPC72G"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const form =document.getElementById("")
function Login() {
    const navigate = useNavigate()
    const MySwal = withReactContent(Swal)
    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
        
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            "username": inputs.username,
            "password": inputs.password,
            "expiresIn": 60000
        });

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("https://www.melivecode.com/api/login", requestOptions)
            .then(response => response.json())
            .then(result => {
                console.log(result)
                if (result.status === "ok") {
                    MySwal.fire({
                        html:<i>{result.message}</i>,
                        icon:"success"
                    }).then((value) => {
                        localStorage.setItem("token",result.accessToken);
                        navigate("./profile")
                    })
                } else{
                    MySwal.fire({
                        html:<i>{result.message}</i>,
                        icon:"error"
                    })
                }
            })
            .catch(error => console.log('error', error));
        console.log(inputs);
    }
    return (
    <div class="container">
            <header class="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 mb-4 border-bottom">
                <a  class="d-flex align-items-center col-md-3 mb-2 mb-md-0 text-dark text-decoration-none">
                    {/* <img style={{border-radius: "50%"}} src="https://i.ytimg.com/vi/NI7wUE7eybU/maxresdefault.jpg" width="40%" height="40%"> */}
                    <h2>LOGO</h2>
                </a>

                <ul class="nav col-12 col-md-auto mb-2 justify-content-center mb-md-0">
                    <li><a href="#" class="nav-link px-2 link-secondary">หน้าหลัก</a></li>
                    <li><a href="#" class="nav-link px-2 link-dark">เกี่ยวกับเรา</a></li>
                    <li><a href="#" class="nav-link px-2 link-dark">เทคโนโลยี AI</a></li>
                    <li><a href="#" class="nav-link px-2 link-dark">บริการ</a></li>
                    <li><a href="#" class="nav-link px-2 link-dark">ติดต่อเรา</a></li>
                </ul>

                <ul>
                    <li style={{ listStyleType :"none",textDecoration:"none" ,color: "#5DADE2"}}><a href="#" class="nav-link px-2 link-lightblue">
                        Guest
                        <i class="fa-solid fa-chevron-down"></i>
                        </a>
                            <ul className='dropdown'>
                                <li style={{ listStyleType :"none",textDecoration:"none" ,color: "#5DADE2"}}><a href="#">โหมดผู้ใช้ทั่วไป</a></li>
                                <ul style={{ listStyleType :"none",textDecoration:"none" ,color: "#5DADE2"}}><a href="#">สมัครสมาชิก</a>
                                    <li style={{ listStyleType :"none",textDecoration:"none" ,color: "#5DADE2"}}><a href="#">สมัครสมาชิก</a></li>
                                    <li style={{ listStyleType :"none",textDecoration:"none" ,color: "#5DADE2"}}><a href="#">Logout</a></li>
                                </ul>
                            </ul>
                        </li>
                </ul>
                
            </header>
        <br/>
        <div id = "form-area">
            <main class="form-signin w-100 m-auto">
                <form id='registerForm' onSubmit={handleSubmit}>
                    
                    <label>อีเมล
                    <br/>
                    <input 
                        className='from-control'
                        type="text" 
                        name="username" 
                        value={inputs.username || ""} 
                        onChange={handleChange}
                        placeholder='username@gmail.com'
                    />
                    </label>
                    <label>รหัสผ่าน
                    <br/>
                        <input 
                            className='from-control'
                            type="password" 
                            name="password" 
                            value={inputs.password || ""} 
                            onChange={handleChange}
                            placeholder='Enter your password'
                    />
                    </label>
                    <br/>
                        <button 
                            type="submit">
                            เข้าสู่ระบบ
                    </button>
                    <p class="mt-5 mb-3 text-muted">ยังไม่มีบัญชีสมาชิก?<a href="Register.js">สมัครสมาชิก</a></p> 
                </form>
            </main>
        </div>
    </div>
    )
}


export default Login
